import React, { useState } from "react";
import axios from "axios";

export default function PayPal({ totalAmount, onPaymentSuccess }) {
    const [showPayPal, setShowPayPal] = useState(false);

    const handlePayment = async () => {
        const paypalScriptUrl = "https://www.paypal.com/sdk/js?client-id=YOUR_CLIENT_ID";
        const paypalSdkScript = document.createElement("script");
        paypalSdkScript.src = paypalScriptUrl;
        paypalSdkScript.async = true;
        document.body.appendChild(paypalSdkScript);

        paypalSdkScript.onload = () => {
            window.paypal
                .Buttons({
                    createOrder: function () {
                        return axios.post("/api/paypal/order", { totalAmount })
                            .then(function (response) {
                                return response.data.orderID;
                            });
                    },
                    onApprove: function (data, actions) {
                        return actions.order.capture().then(async function (details) {
                            setShowPayPal(false);
                            await onPaymentSuccess();
                        });
                    },
                })
                .render("#paypal-button-container");
            setShowPayPal(true);
        };
    };

    return (
        <>
            {!showPayPal && (
                <button type="button" onClick={handlePayment}>
                    Submit and Proceed to Payment
                </button>
            )}
            {showPayPal && (
                <div id="paypal-button-container"></div>
            )}
        </>
    );
}
