import React, { useState, useContext } from "react";
import axios from "axios";
import { ShopContext } from "../context/shop-context";
import allProducts from "../data/fulldata.js";

export default function ShippingForm() {
    const [formData, setFormData] = useState({
        name: "",
        address: "",
        town: "",
        state: "",
        zip: "",
    });

    const { cartItems } = useContext(ShopContext); // Access cartItems from context

    const [formErrors, setFormErrors] = useState({});

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const errors = validateForm(formData);
        if (Object.keys(errors).length === 0) {
            // Include cartItems in form submission
            const cartItemsWithData = Object.keys(cartItems).map((itemId) => {
                const itemInfo = allProducts.find((product) => product.id === itemId);
                return {
                    id: itemId,
                    name: itemInfo.name,
                    quantity: cartItems[itemId],
                };
            });

            // Add product name and quantity to form data
            const updatedFormData = {
                ...formData,
                cartItems: JSON.stringify(cartItemsWithData)
            };

            console.log("cartItems:", cartItems); // Log cartItems to console

            // Send updatedFormData to MongoDB
            try {
                await axios.post("http://localhost:5000/api/shipping", updatedFormData);

                // Clear form data
                setFormData({
                    name: "",
                    address: "",
                    town: "",
                    state: "",
                    zip: "",
                });
            } catch (error) {
                console.error("Error sending data to MongoDB:", error);
            }
        } else {
            setFormErrors(errors);
        }
    };

    const validateForm = (data) => {
        const errors = {};
        if (!data.name) {
            errors.name = 'Please enter your name';
        }
        if (!data.address) {
            errors.address = 'Please enter your street address';
        }
        if (!data.town) {
            errors.town = 'Please enter your town';
        }
        if (!data.state) {
            errors.state = 'Please enter your state';
        }
        if (!data.zip) {
            errors.zip = 'Please enter your zip code';
        } else if (!/^[0-9]{5}(?:-[0-9]{4})?$/.test(data.zip)) {
            errors.zip = 'Please enter a valid zip code';
        }
        return errors;
    };

    return (
        <form onSubmit={handleSubmit}>
            <h1>  Shipping Info: </h1>
            {/* Rest of the form */}
        </form>
    );
}
