import React, { useContext, useState } from "react";
import { ShopContext } from "../context/shop-context";
import CartItem from "../components/CartItem";
import allProducts from "../data/fulldata";
import "../cart.css";
import { useNavigate } from "react-router-dom";
import { PayPalButtons } from "@paypal/react-paypal-js";

export default function Cart() {
  const { cartItems, getTotalCartAmount, getDefaultcart } = useContext(ShopContext);
  const totalAmount = getTotalCartAmount();
  const navigate = useNavigate();
  const [succeeded, setSucceeded] = useState(false);
  const [paypalErrorMessage, setPaypalErrorMessage] = useState("");
  const [orderID, setOrderID] = useState(false);
  const [billingDetails, setBillingDetails] = useState("");



  // creates a paypal order
  const createOrder = (data, actions) => {
    return actions.order
      .create({
        purchase_units: [
          {

            amount: {
              value: getTotalCartAmount(),
            },
          },
        ],

      })
      .then((orderID) => {
        setOrderID(orderID);
        return orderID;
      });
  };
  // handles when a payment is confirmed for paypal
  const onApprove = (data, actions) => {
    return actions.order.capture().then(function (details) {
      console.log(JSON.stringify(JSON.parse(details), null, 2));
      //setBillingDetails(payer);
      setSucceeded(true);
    });
  }; // handles payment errors
  const onError = (data, actions) => {
    setPaypalErrorMessage("Something went wrong with your payment");
  };

  if (succeeded === false) {
    return (
      <aside className="cart">
        <div>
          <h1>Your Cart:</h1>
        </div>
        <div>
          {allProducts.map((product) => {
            if (cartItems[product.id] !== 0) {
              return <CartItem data={product} />;
            }
          })}
        </div>

        <div className="checkout">
          <p>Subtotal: ${totalAmount}</p>
          <button onClick={() => navigate("/")}>Continue Shopping</button>

          <PayPalButtons
            style={{
              color: "blue",
              shape: "pill",
              label: "pay",
              tagline: false,
              layout: "horizontal",
            }}
            createOrder={createOrder}
            onApprove={onApprove}
            forceReRender={[totalAmount]}
          />
        </div>
        <div>
        </div>
      </aside>
    );
  } else {
    return (
      <div>
        <h1>Thank you for your purchase!</h1>
        <p>Order ID: {orderID}</p>
        <p>Shipping Address: {billingDetails}</p>
        <p>Shipping City: {billingDetails.address.city}</p>
        <p>Shipping State: {billingDetails.address.state}</p>
        <p>Shipping Zip: {billingDetails.address.postal_code}</p>
        <button onClick={() => navigate("/")}>Continue Shopping</button>
      </div>
    );
  }
}
